import React from 'react';
import ReactDOM from "react-dom";
import './App.css';
//import Videos from './components/videos/videos'

function App() {
  return (
    <div className="App">
      <header id='header'>Youtube Channel API</header>
      <QueryForm/>
    </div>
  );
}

class QueryForm extends React.Component {
  constructor () {
    super();
    this.state = {
        videos: []
    }
  }

  componentDidMount() {
    fetch('http://localhost:8080/api/channel')
    .then(res => res.json())
    .then(videos => this.setState({videos: videos}, () => console.log('Videos fetched: ', videos)));
  }

  SubmitHandler = (event) => {
    event.preventDefault();
    var chan = ReactDOM.findDOMNode(this.refs.channel).value;
    var results = ReactDOM.findDOMNode(this.refs.maxResults).value;
    var phrase = ReactDOM.findDOMNode(this.refs.keyPhrase).value;
    console.log(chan,results,phrase);

    fetch('http://localhost:8080/api/channel?channel='+chan+'&maxResults='+results+'&keyPhrase='+phrase)
    .then(res => res.json())
    .then(videos => this.setState({videos: videos}, () => console.log('Videos fetched: ', videos)));

  }

  render() {
    return (
      <div className="App">
        <div id="inputForm">
          <form onSubmit={this.SubmitHandler}>
            <p>Specify Search Results:</p>
            <p>Channel: <input ref="channel" id="channel" type="text"/></p>
            <p>Number of Videos: <input ref="maxResults" id="maxResults" type="text"/></p>
            <p>Key Terms: <input ref="keyPhrase" id="keyTerms" type="text"/></p>
            <a></a>
            <input type='submit'/>
          </form>
          <iframe src="https://www.youtube.com/embed/watch?v=JztgJT547Vg" width="1920" height="1080"></iframe>
        </div>
        <div id="videoGrid">
          <ul id="videos">
              {this.state.videos.map(video => 
                <li class="thumbnail" id={video.id.videoId}>
                  <h1 class="title">{video.snippet.title}</h1>
                  <img class="icon" src={video.snippet.thumbnails.default.url}></img>
                </li>
              )}
          </ul>
        </div>
      </div>
    );
  }
}

export default App;
